mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM AS RN FROM ( SELECT B.BO_NO, B.ME_NO, B.TYPE, B.TITLE, B.CONTENT, B.INSERT_TIME, B.UPDATE_TIME, B.ANSWER, B.ANSWER_INSERT_TIME, B.ANSWER_UPDATE_TIME , M.NAME FROM BOARD B JOIN MEMBER M ON M.ME_NO = B.ME_NO WHERE B.TYPE = #{type}
AND B.ANSWER IS NOT NULL
AND B.ANSWER IS NULL
ORDER BY B.${sortField} ${sortOrder} NULLS LAST ) A ) WHERE RN BETWEEN #{startRow} and #{endRow}

SELECT COUNT(*) FROM BOARD B JOIN MEMBER M ON M.ME_NO = B.ME_NO WHERE B.TYPE = #{type}
AND B.ANSWER IS NOT NULL
AND B.ANSWER IS NULL
