mapper.select:
SELECT * FROM ( SELECT A.*, ROWNUM AS RN FROM ( SELECT ME_NO, TYPE, EMAIL, PW, NAME, ADDRESS, ADDRESS_DETAIL, LATITUDE, LONGITUDE, PHONE_NUM, EMAIL_NOTIFICATION, PROFILE_PHOTO, JOIN_APPROVAL, JOIN_APPROVAL_TIME, BUSINESS_NUMBER, SHORT_DESCRIPTION, MAX_DISTANCE, DESCRIPTION, WORK_HOURS, POINT, STATUS, M.INSERT_TIME, M.UPDATE_TIME , C.CA_NO , C.CA_NAME , CR.CA_NO AS CA_ROOT_NO , CR.CA_NAME AS CA_ROOT_NAME ,RESULT.AVERAGE_SCORE ,RESULT.REVIEW_COUNT FROM MEMBER M LEFT JOIN CATEGORY C ON C.CA_NO = M.CA_NO LEFT JOIN CATEGORY CR ON CR.CA_NO = C.ROOT_NO LEFT JOIN ( SELECT MASTER_NO, ROUND( AVG(STAR_SCORE), 2) AS AVERAGE_SCORE, COUNT(STAR_SCORE) AS REVIEW_COUNT FROM ( SELECT * FROM REVIEW R INNER JOIN REQUEST R2 ON R.REQ_NO = R2.REQ_NO ) GROUP BY MASTER_NO ) RESULT ON M.ME_NO = RESULT.MASTER_NO WHERE M.TYPE = #{type}
AND M.JOIN_APPROVAL = #{joinApproval}
AND CR.CA_NO = #{rootNo}
AND C.CA_NO = #{caNo}
AND C.CA_NO = #{subCate}
ORDER BY M.${sortField} ${sortOrder} NULLS LAST ) A ) WHERE RN BETWEEN #{startRow} and #{endRow}

SELECT COUNT(*)
FROM MEMBER M
LEFT JOIN CATEGORY C
ON C.CA_NO = M.CA_NO
LEFT JOIN CATEGORY CR
ON CR.CA_NO = C.ROOT_NO
WHERE M.TYPE = #{type}

	AND M.JOIN_APPROVAL = #{joinApproval}


	AND CR.CA_NO = #{rootNo}


	AND C.CA_NO = #{caNo}

SELECT ME_NO, TYPE, EMAIL, PW, NAME, ADDRESS, ADDRESS_DETAIL, LATITUDE, LONGITUDE, PHONE_NUM, EMAIL_NOTIFICATION, PROFILE_PHOTO, JOIN_APPROVAL, JOIN_APPROVAL_TIME, BUSINESS_NUMBER, SHORT_DESCRIPTION, MAX_DISTANCE, DESCRIPTION, WORK_HOURS, POINT, STATUS, M.INSERT_TIME, M.UPDATE_TIME , C.CA_NO , C.CA_NAME , CR.CA_NO AS CA_ROOT_NO , CR.CA_NAME AS CA_ROOT_NAME ,RESULT.AVERAGE_SCORE ,RESULT.REVIEW_COUNT FROM MEMBER M LEFT JOIN CATEGORY C ON C.CA_NO = M.CA_NO LEFT JOIN CATEGORY CR ON CR.CA_NO = C.ROOT_NO LEFT JOIN ( SELECT MASTER_NO, ROUND( AVG(STAR_SCORE), 2) AS AVERAGE_SCORE, COUNT(STAR_SCORE) AS REVIEW_COUNT FROM ( SELECT * FROM REVIEW R INNER JOIN REQUEST R2 ON R.REQ_NO = R2.REQ_NO ) GROUP BY MASTER_NO ) RESULT ON M.ME_NO = RESULT.MASTER_NO WHERE 1 = 1
AND M.ME_NO = #{meNo}
AND M.EMAIL = #{email} AND M.PW = #{pw}
AND M.EMAIL = #{email}

SELECT pw
FROM MEMBER
WHERE ME_NO = #{meNo}
