mapper.select:
SELECT *
FROM (
SELECT A.*, ROWNUM AS RN
FROM (
SELECT ME_NO, TYPE, EMAIL, PW, NAME, ADDRESS, ADDRESS_DETAIL, LATITUDE, LONGITUDE, PHONE_NUM, EMAIL_NOTIFICATION, PROFILE_PHOTO, JOIN_APPROVAL, JOIN_APPROVAL_TIME, BUSINESS_NUMBER, SHORT_DESCRIPTION, MAX_DISTANCE, DESCRIPTION, WORK_HOURS, POINT, STATUS, M.INSERT_TIME, M.UPDATE_TIME
, C.CA_NO
, C.CA_NAME
, CR.CA_NO AS CA_ROOT_NO
, CR.CA_NAME AS CA_ROOT_NAME
FROM MEMBER M
LEFT JOIN CATEGORY C
ON C.CA_NO = M.CA_NO
LEFT JOIN CATEGORY CR
ON CR.CA_NO = C.ROOT_NO
WHERE M.TYPE = #{type}

	AND M.JOIN_APPROVAL = #{joinApproval}


	AND CR.CA_NO = #{rootNo}


	AND C.CA_NO = #{caNo}

ORDER BY M.${sortField} ${sortOrder} NULLS LAST
) A
)
WHERE RN BETWEEN #{startRow} and #{endRow}

SELECT COUNT(*)
FROM MEMBER M
LEFT JOIN CATEGORY C
ON C.CA_NO = M.CA_NO
LEFT JOIN CATEGORY CR
ON CR.CA_NO = C.ROOT_NO
WHERE M.TYPE = #{type}

	AND M.JOIN_APPROVAL = #{joinApproval}


	AND CR.CA_NO = #{rootNo}


	AND C.CA_NO = #{caNo}

SELECT ME_NO, TYPE, EMAIL, PW, NAME, ADDRESS, ADDRESS_DETAIL, LATITUDE, LONGITUDE, PHONE_NUM, EMAIL_NOTIFICATION, PROFILE_PHOTO, JOIN_APPROVAL, JOIN_APPROVAL_TIME, BUSINESS_NUMBER, SHORT_DESCRIPTION, MAX_DISTANCE, DESCRIPTION, WORK_HOURS, POINT, STATUS, M.INSERT_TIME, M.UPDATE_TIME , C.CA_NO , C.CA_NAME , CR.CA_NO AS CA_ROOT_NO , CR.CA_NAME AS CA_ROOT_NAME FROM MEMBER M LEFT JOIN CATEGORY C ON C.CA_NO = M.CA_NO LEFT JOIN CATEGORY CR ON CR.CA_NO = C.ROOT_NO WHERE 1 = 1
AND M.ME_NO = #{meNo}
AND M.EMAIL = #{email} AND M.PW = #{pw}

mapper.insert:
INSERT INTO MEMBER (me_no, type, email, pw, name, phone_num, address, address_detail, latitude, longitude, email_notification, business_number, short_description, max_distance, description, work_hours, ca_no, join_approval, point, status)
VALUES (MEMBER_SEQ.NEXTVAL, #{type}, #{email}, #{pw}, #{name}, #{phoneNum}, #{address}, #{addressDetail}, #{latitude}, #{longitude}, #{emailNotification}, #{businessNumber}, #{shortDescription}, #{maxDistance}, #{description}, #{workHours}, #{caNo}, #{joinApproval}, #{point}, #{status}
)

mapper.select:
SELECT pw
FROM MEMBER
WHERE ME_NO = #{meNo}

SELECT ME_NO, NAME, ADDRESS, WORK_HOURS
FROM MEMBER
WHERE ME_NO = #{meNo}
